tinymce.PluginManager.add('files', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('files', {
        icon: true,
        image: '/static/js/tinymce/plugins/files/tiny-file.png',
        title: 'Вставка файла',
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Вставка файла',
                file: tinymce.activeEditor.settings['files_path'],
                width: 700,
                height: 480
            });
        }
    });
});
