tinymce.PluginManager.add('product', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('product', {
        icon: true,
        image: url + '/tiny-product.png',
        title: 'Вставка товара',
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Вставка товара',
                file: tinymce.activeEditor.settings['products_path'],
                width: 700,
                height: 480
            });
        }
    });
});
